import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Menu from './components/Menu';
import OfflineBanner from './components/OfflineBanner';
import Login from './pages/Login';
import Desafios from './pages/Desafios';
import Quiz from './pages/Quiz';
import RelatorioProfessor from './pages/RelatorioProfessor';
import OfflineBox from './pages/OfflineBox';

function App() {
  const [aluno, setAluno] = useState(null);
  const [offline, setOffline] = useState(false);
  const [maestria, setMaestria] = useState({}); // {quizId: pontos}

  return (
    <Router>
      <div style={{ display: 'flex', minHeight: '100vh', background: 'linear-gradient(120deg, #6a11cb 0%, #2575fc 100%)' }}>
        <Menu aluno={aluno} offline={offline} setOffline={setOffline} />
        <div style={{ flex: 1, padding: '32px', minHeight: '100vh' }}>
          {offline && <OfflineBanner />}
          <Routes>
            <Route path="/" element={aluno ? <Navigate to="/desafios" /> : <Login setAluno={setAluno} />} />
            <Route path="/login" element={<Login setAluno={setAluno} />} />
            <Route path="/desafios" element={aluno ? <Desafios aluno={aluno} maestria={maestria} /> : <Navigate to="/login" />} />
            <Route path="/quiz/:id" element={aluno ? <Quiz aluno={aluno} offline={offline} maestria={maestria} setMaestria={setMaestria} /> : <Navigate to="/login" />} />
            <Route path="/relatorio" element={<RelatorioProfessor offline={offline} />} />
            <Route path="/offlinebox" element={<OfflineBox offline={offline} />} />
          </Routes>
        </div>
      </div>
    </Router>
  );
}

export default App;
