import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Menu from './components/Menu';
import Login from './pages/Login';
import Dashboard from './pages/Dashboard';
import Alunos from './pages/Alunos';
import Desempenho from './pages/Desempenho';
import Conteudo from './pages/Conteudo';
import Missoes from './pages/Missoes';
import Trilhas from './pages/Trilhas';
import Recompensas from './pages/Recompensas';
import Configuracoes from './pages/Configuracoes';

function App() {
  const [professor, setProfessor] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Verificar se há um professor logado no localStorage
    const professorSalvo = localStorage.getItem('professor');
    if (professorSalvo) {
      try {
        setProfessor(JSON.parse(professorSalvo));
      } catch (error) {
        console.error('Erro ao carregar dados do professor:', error);
      }
    }
    setLoading(false);
  }, []);

  if (loading) {
    return (
      <div style={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: 'white',
        fontSize: 18
      }}>
        Carregando...
      </div>
    );
  }

  return (
    <Router>
      {professor ? (
        <div style={{ display: 'flex' }}>
          <Menu professor={professor} />
          <div style={{ flex: 1, marginLeft: 300 }}>
            <Routes>
              <Route path="/" element={<Dashboard professor={professor} />} />
              <Route path="/alunos" element={<Alunos professor={professor} />} />
              <Route path="/desempenho" element={<Desempenho professor={professor} />} />
              <Route path="/conteudo" element={<Conteudo professor={professor} />} />
              <Route path="/missoes" element={<Missoes professor={professor} />} />
              <Route path="/trilhas" element={<Trilhas professor={professor} />} />
              <Route path="/recompensas" element={<Recompensas professor={professor} />} />
              <Route path="/configuracoes" element={<Configuracoes professor={professor} />} />
              <Route path="/login" element={<Navigate to="/" replace />} />
              <Route path="*" element={<Navigate to="/" replace />} />
            </Routes>
          </div>
        </div>
      ) : (
        <Routes>
          <Route path="*" element={<Login setProfessor={setProfessor} />} />
        </Routes>
      )}
    </Router>
  );
}

export default App;
